(179, 1, '2024-01-19 11:54:29', '2024-01-19 11:54:29', '<!-- wp:template-part {\"slug\":\"header\",\"theme\":\"child-theme-starter/theme\",\"tagName\":\"header\",\"area\":\"header\"} /-->\n\n<!-- wp:group {\"layout\":{\"type\":\"default\"}} -->\n<div class=\"wp-block-group\"><!-- wp:post-title {\"textAlign\":\"center\",\"level\":1} /--></div>\n<!-- /wp:group -->\n\n<!-- wp:group {\"tagName\":\"main\",\"style\":{\"spacing\":{\"padding\":{\"top\":\"var:preset|spacing|20\",\"bottom\":\"var:preset|spacing|20\"}}},\"backgroundColor\":\"secondary\",\"layout\":{\"type\":\"constrained\"}} -->\n<main class=\"wp-block-group has-secondary-background-color has-background\" style=\"padding-top:var(--wp--preset--spacing--20);padding-bottom:var(--wp--preset--spacing--20)\"><!-- wp:post-content {\"lock\":{\"move\":false,\"remove\":true},\"align\":\"wide\",\"layout\":{\"type\":\"default\"}} /--></main>\n<!-- /wp:group -->\n\n<!-- wp:group {\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group\"><!-- wp:group {\"align\":\"wide\",\"layout\":{\"type\":\"flex\",\"orientation\":\"vertical\"}} -->\n<div class=\"wp-block-group alignwide\"><!-- wp:paragraph -->\n<p>This page is set up as a custom template under theme/templates/style-guide.html</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:group --></div>\n<!-- /wp:group -->\n\n<!-- wp:group {\"style\":{\"spacing\":{\"padding\":{\"top\":\"var:preset|spacing|30\",\"bottom\":\"var:preset|spacing|30\"}}},\"backgroundColor\":\"light\",\"layout\":{\"type\":\"constrained\"},\"metadata\":{\"name\":\"Headers\"}} -->\n<div class=\"wp-block-group has-light-background-color has-background\" style=\"padding-top:var(--wp--preset--spacing--30);padding-bottom:var(--wp--preset--spacing--30)\"><!-- wp:group {\"align\":\"wide\",\"layout\":{\"type\":\"default\"}} -->\n<div class=\"wp-block-group alignwide\"><!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">H1 The quick brown fox jumped over the lazy dog</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">H2 The quick brown fox jumped over the lazy dog</h2>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3 class=\"wp-block-heading\">H3 The quick brown fox jumped over the lazy dog</h3>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4 class=\"wp-block-heading\">H4 The quick brown fox jumped over the lazy dog</h4>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":5} -->\n<h5 class=\"wp-block-heading\">H5 The quick brown fox jumped over the lazy dog</h5>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":6} -->\n<h6 class=\"wp-block-heading\">H6 The quick brown fox jumped over the lazy dog</h6>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>This is some body copy. It can have some styling like <strong>bold text</strong> or <em>italic text</em>, or maybe there will be some links embedded into the copy. Taleggio cheeseburger bocconcini. Brie when the cheese comes out everybody\'s happy chalk and cheese port-salut mozzarella cheesy grin cheddar cheese strings. Cheddar cheeseburger blue castello stinking bishop when the cheese comes out everybody\'s happy ricotta emmental swiss. Queso parmesan mascarpone cheeseburger cow cow cut the cheese jarlsberg. Cauliflower cheese dolcelatte.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:group --></div>\n<!-- /wp:group -->\n\n<!-- wp:group {\"style\":{\"spacing\":{\"padding\":{\"top\":\"var:preset|spacing|30\",\"bottom\":\"var:preset|spacing|30\"}}},\"layout\":{\"type\":\"constrained\"},\"metadata\":{\"name\":\"Colors\"}} -->\n<div class=\"wp-block-group\" style=\"padding-top:var(--wp--preset--spacing--30);padding-bottom:var(--wp--preset--spacing--30)\"><!-- wp:group {\"align\":\"wide\",\"layout\":{\"type\":\"default\"}} -->\n<div class=\"wp-block-group alignwide\"><!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\">Colors</h1>\n<!-- /wp:heading -->\n\n<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div clas