(260, 1, '2024-04-02 20:53:30', '2024-04-02 20:53:30', '<div>\r\n<div class=\"grid grid-cols-4 gap-3\">\r\n<div>\r\n<div class=\"bg-base-100 px-3 py-1.5\">Base 100</div>\r\n<div class=\"bg-base-200 px-3 py-1.5\">Base 200</div>\r\n<div class=\"bg-base-300 px-3 py-1.5\">Base 300</div>\r\n<small class=\"block px-3 py-1 text-inherit\">\r\nText Base Content\r\n</small>\r\n\r\n</div>\r\n<div>\r\n<div class=\"px-3 py-6 bg-primary text-primary-content\">Primary\r\n<small class=\"block\">(text color is primary-content)</small></div>\r\n<div class=\"px-3 py-1 text-primary-contrast\">* Primary Contrast</div>\r\n</div>\r\n<div class=\"\">\r\n<div class=\"px-3 py-6 bg-secondary text-secondary-content\">Secondary\r\n<small class=\"block\">(text color is secondary-content)</small></div>\r\n<div class=\"px-3 py-1 text-secondary-contrast\">* Secondary Contrast</div>\r\n</div>\r\n<div class=\"\">\r\n<div class=\"px-3 py-6 bg-accent text-accent-content\">Accent\r\n<small class=\"block\">(text color is accent-content)</small></div>\r\n<div class=\"px-3 py-1 text-accent-contrast\">* Accent Contrast</div>\r\n</div>\r\n<div class=\"\">\r\n<div class=\"px-3 py-4 bg-neutral text-neutral-content\">Neutral\r\n<small class=\"block\">(text color is neutral-content)</small></div>\r\n</div>\r\n<div class=\"\">\r\n<div class=\"px-3 py-4 bg-info text-info-content\">Info\r\n<small class=\"block\">(text color is info-content)</small></div>\r\n</div>\r\n<div class=\"\">\r\n<div class=\"px-3 py-4 bg-success text-success-content\">Success\r\n<small class=\"block\">(text color is success-content)</small></div>\r\n</div>\r\n<div class=\"\">\r\n<div class=\"px-3 py-4 bg-warning text-warning-content\">Warning\r\n<small class=\"block\">(text color is warning-content)</small></div>\r\n</div>\r\n<div class=\"\">\r\n<div class=\"px-3 py-4 bg-error text-error-content\">Error\r\n<small class=\"block\">(text color is error-content)</small></div>\r\n</div>\r\n<div class=\"p-3 text-white bg-danger\">* Danger</div>\r\n</div>\r\n<div class=\"grid grid-cols-4 gap-3 my-3\">\r\n<div class=\"\">\r\n<div class=\"p-3 bg-mid-0\">* mid-0</div>\r\n<div class=\"p-3 bg-mid-100\">* mid-100</div>\r\n<div class=\"p-3 bg-mid-200\">* mid-200</div>\r\n<div class=\"p-3 bg-mid-300 text-mid-1000\">* mid-300</div>\r\n<div class=\"p-3 bg-mid-400 text-mid-1000\">* mid-400</div>\r\n<div class=\"p-3 bg-mid-500 text-mid-1000\">* mid-500</div>\r\n<div class=\"p-3 bg-mid-600 text-mid-0\">* mid-600</div>\r\n<div class=\"p-3 bg-mid-700 text-mid-0\">* mid-700</div>\r\n<div class=\"p-3 bg-mid-800 text-mid-0\">* mid-800</div>\r\n<div class=\"p-3 bg-mid-900 text-mid-0\">* mid-900</div>\r\n<div class=\"p-3 bg-mid-1000 text-mid-0\">* mid-1000</div>\r\n</div>\r\n<div class=\"\">\r\n<div class=\"p-3 py-5 text-black bg-white\">White</div>\r\n<div class=\"p-3 py-5 text-black bg-light\">Light</div>\r\n<div class=\"p-3 py-5 text-black bg-mid\">Mid</div>\r\n<div class=\"p-3 py-5 text-white bg-dark\">Dark</div>\r\n<div class=\"p-3 py-5 text-white bg-black\">Black</div>\r\n<small>\r\nThe colors above do not switch between dark and light mode.\r\n</small>\r\n\r\n</div>\r\n</div>\r\n</div>', 'Colors', '', 'publish', 'closed', 'closed', '', 'colors', '', '', '2025-10-27 18:13:08', '2025-10-27 18:13:08', '', 174, 'https://wp-starter.paulstroot.localhost/?page_id=260', 0, 'page', '', 0),
